mapper.update:
update user
name=#{name}, email=#{email}, password=password(#{password}), tel=#{tel}, major=#{major}, introduce=#{introduce}, profile_photo=#{profilePhoto}
where user_no=#{userNo}

mapper.select:

select * from user
where 
  name like #{keywordPattern}
  or email like #{keywordPattern}
  or tel like #{keywordPattern}
